    render() {
      const status = 'Next player: X';

      return (
        <div>
          <div className="status">{status}</div>
          <div className="board-row">
            {this.renderCard(0)}
            {this.renderCard(1)}
            {this.renderCard(2)}
          </div>
          <div className="board-row">
            {this.renderCard(3)}
            {this.renderCard(4)}
            {this.renderCard(5)}
          </div>
          <div className="board-row">
            {this.renderCard(6)}
            {this.renderCard(7)}
            {this.renderCard(8)}
          </div>
        </div>
      );
    }
  }

  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }

  // ========================================

  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
